sys_variable_value.value:
(function execute(inputs, outputs) {
  var tableName = inputs.table;
  var scope = inputs.scope;
  var fields = inputs.fields;
  var propertyName = scope + ".record.summary.input_fields." + tableName;

  //get the fieldNames & store them in a systemProperty as string. Summarize feature will work based on these fields.

  var fieldsName = [];
  var getFields = new GlideRecord("sys_dictionary");
  getFields.addEncodedQuery("sys_idIN" + fields + "^ORelementIN" + fields);
  getFields.setLimit(100); //bogus limit - not expecting more than 100 fields in any case.
  getFields.query();
  while (getFields.next()) {
    fieldsName.push(getFields.getValue("element"));
  }

  if (fieldsName.length > 0) {
    var createProperty = new GlideRecord("sys_properties");
    createProperty.newRecord();
    createProperty.setValue("suffix", propertyName);
    createProperty.setValue("name", propertyName);
    createProperty.setValue(
      "description",
      "This system property over-rides the system property [sn_uxc_gen_ai.record.summary.input_fields] for table [" +
        tableName +
        "]. Summarize feature works based on these fields."
    );
    createProperty.setValue("type", "string");
    createProperty.setValue("value", fieldsName.join(","));
    createProperty.insert();
  }
  //Now create Summarize Action, The below script assumes that Now Assist for ITSM is already installed. 

  	var sumActionSysID;
    var createButton = new GlideRecord("sys_ui_action");
    createButton.newRecord();
    createButton.setValue("name", "Summarize Record");
    createButton.setValue("table", tableName);
    createButton.setValue("order", -1000);
    //createButton.setValue("action_name", "summarize");
    createButton.setValue("active", true);
    createButton.setValue("show_insert", true);
    createButton.setValue("show_update", true);
  	createButton.setValue("client", false);
  	createButton.setValue("form_button", true);
    createButton.setValue("messages", "<b>Summary:</b> Retrieving the summary...");
    //createButton.setValue("onclick", "summarize()");
    createButton.setValue("condition", "!current.isNewRecord()");
    createButton.setValue("script", gs.getProperty("summarize_server_code"));
    //createButton.setValue(
    //  "client_script_v2",
    //  gs.getProperty("x_snc_crwf_autom_0.u_summarize.workspace_client_script")
    //);
    createButton.setValue("form_button_v2", true);
    createButton.setValue("format_for_configurable_workspace", true);
    sumActionSysID = createButton.insert();


  //Let's make sure the UX Form Action was added successfully and the button is visible in the workspace

  var checkUXFormAction = new GlideRecord("sys_ux_form_action");
  checkUXFormAction.addEncodedQuery("active=true^table="+tableName+"^ui_action="+sumActionSysID);
  checkUXFormAction.setLimit(1);
  checkUXFormAction.query();
  if (checkUXFormAction.hasNext()) {
    outputs.status = "Success";
  } else {
    outputs.status = "Error";
  }
})(inputs, outputs);

