sys_variable_value.value:
(function execute(inputs, outputs) {
  var response = JSON.parse(inputs.response_body);
  if (inputs.error_code == 0) {
    outputs.id = response.id;
    outputs.object = response.object;
    outputs.created = response.created;
    outputs.model = response.model;
    outputs.usage = response.usage;
    outputs.choices = response.choices;
    outputs.action_status_code = "0";
    outputs.action_status_message = "Success";
  } else {
    outputs.action_status_code = inputs.status_code;

    if (response.error) {
      outputs.action_status_message = response.error.message ? response.error.message : response.error.code;
    } else {
      outputs.action_status_message = response.message;
    }
  }
})(inputs, outputs);

