sys_variable_value.value:
(function execute(inputs, outputs) {
    var requestBody = {
        "model": inputs.model,
        "messages": inputs.messages
    };
    if (inputs.max_tokens) {
        if (isNaN(inputs.max_tokens) || isNaN(parseInt(inputs.max_tokens))) {
            throw new Error("'" + inputs.max_tokens + "' is not of type 'integer' - 'max_tokens'");
        }
        requestBody["max_tokens"] = parseInt(inputs.max_tokens);
    }
    if (inputs.temperature) {
        if (isNaN(inputs.temperature) || isNaN(parseFloat(inputs.temperature))) {
            throw new Error("'" + inputs.temperature + "' is not of type 'number' - 'temperature'");
        }
        requestBody["temperature"] = parseFloat(inputs.temperature);
    }
    if (inputs.top_p) {
        if (isNaN(inputs.top_p) || isNaN(parseFloat(inputs.top_p))) {
            throw new Error("'" + inputs.top_p + "' is not of type 'number' - 'top_p'");
        }
        requestBody["top_p"] = parseFloat(inputs.top_p);
    }
    if (inputs.n) {
        if (isNaN(inputs.n) || isNaN(parseInt(inputs.n))) {
            throw new Error("'" + inputs.n + "' is not of type 'integer' - 'n'");
        }
        requestBody["n"] = parseInt(inputs.n);
    }
    if (inputs.stop && !(inputs.stop.length == 1 && inputs.stop[0] == "")) {
        requestBody["stop"] = inputs.stop;
    }
    if (inputs.presence_penalty) {
        if (isNaN(inputs.presence_penalty) || isNaN(parseFloat(inputs.presence_penalty))) {
            throw new Error("'" + inputs.presence_penalty + "' is not of type 'number' - 'presence_penalty'");
        }
        requestBody["presence_penalty"] = parseFloat(inputs.presence_penalty);
    }
    if (inputs.frequency_penalty) {
        if (isNaN(inputs.frequency_penalty) || isNaN(parseFloat(inputs.frequency_penalty))) {
            throw new Error("'" + inputs.frequency_penalty + "' is not of type 'number' - 'frequency_penalty'");
        }
        requestBody["frequency_penalty"] = parseFloat(inputs.frequency_penalty);
    }
    if (inputs.logit_bias) {
        requestBody["logit_bias"] = inputs.logit_bias;
    }
    if (inputs.user) {
        requestBody["user"] = inputs.user;
    }
    if(inputs.send_json_response) {
        requestBody["response_format"] = {
            type: "json_object"
        };
    }
    outputs.request_body = JSON.stringify(requestBody);

})(inputs, outputs);

